import React , {useEffect, useState} from 'react'
import axios from 'axios'
import { Link , useHistory} from 'react-router-dom'
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';

const NewHome = () => {
  const history = useHistory();

  const logout = () => {
    setLoginStatus(null);
    axios.get("http://localhost:5000/api/logout").then((response)=>{
      response.data.loggedIn = false 
    });
    history.push('/login')
  }
  axios.defaults.withCredentials = true;
  const [loginStatus,setLoginStatus] = useState()

  useEffect(()=>{
    axios.get("http://localhost:5000/api/login").then((response)=>{
      if (response.data.loggedIn === true) {
        setLoginStatus(response.data.user[0].username)
      }
    })
  }, [])
  const itemData = [
    {
      img: 'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
      title: 'Breakfast',
    },
    {
      img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
      title: 'Burger',
    },
    {
      img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
      title: 'Camera',
    },
    {
      img: 'https://images.unsplash.com/photo-1444418776041-9c7e33cc5a9c',
      title: 'Coffee',
    },
    {
      img: 'https://images.unsplash.com/photo-1533827432537-70133748f5c8',
      title: 'Hats',
    },
    {
      img: 'https://images.unsplash.com/photo-1558642452-9d2a7deb7f62',
      title: 'Honey',
    },
    {
      img: 'https://images.unsplash.com/photo-1516802273409-68526ee1bdd6',
      title: 'Basketball',
    },
    {
      img: 'https://images.unsplash.com/photo-1518756131217-31eb79b20e8f',
      title: 'Fern',
    },
    {
      img: 'https://images.unsplash.com/photo-1597645587822-e99fa5d45d25',
      title: 'Mushrooms',
    },
    {
      img: 'https://images.unsplash.com/photo-1567306301408-9b74779a11af',
      title: 'Tomato basil',
    },
    {
      img: 'https://images.unsplash.com/photo-1471357674240-e1a485acb3e1',
      title: 'Sea star',
    },
    {
      img: 'https://images.unsplash.com/photo-1589118949245-7d38baf380d6',
      title: 'Bike',
    },
  ];
  return (
    <div>
    <div style={{
      backgroundColor: "#019B79",
      color: "white",
      paddingTop: "10px"
    }}>
    <h1 style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold"}} >Workflow Management System</h1>
    <nav style={{
        color: "Yellow",
        alignContent: "center",
        textAlign:"center"
      }}>
      <Link style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold",marginBottom: "20px"}} className='btn8' to="/"> Home </Link>
      {loginStatus && (<Link style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold",marginBottom: "10px"}} className='btn8' to="/board"> Board </Link>)}
      {!loginStatus && (<Link style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold"}} className='btn8' to="/signup"> Signup </Link>)}
      <Link style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold"}} className='btn8' to="/login"> Login </Link>
      {loginStatus && (<button style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold"}} className='btn8'  onClick={logout}> Logout </button>)} 
           </nav>
           <h3 style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold"}}>User: {loginStatus}</h3>
  </div>
      <div>
        <h2>Welcome to Workflow Management</h2>
        <ImageList sx={{ width: 500, height: 450 }} cols={3} rowHeight={164}>
      {itemData.map((item) => (
        <ImageListItem key={item.img}>
          <img
            src={`${item.img}?w=164&h=164&fit=crop&auto=format`}
            srcSet={`${item.img}?w=164&h=164&fit=crop&auto=format&dpr=2 2x`}
            alt={item.title}
            loading="lazy"
          />
        </ImageListItem>
      ))}
    </ImageList>
      </div>
  </div>
  )
}

export default NewHome
