import React, {useState,useEffect} from 'react';
import {Link} from "react-router-dom";
import "./Doing.css";
import {toast} from "react-toastify"
import axios from "axios";


const Home1 = () => {
    const [data, setData] = useState([]); 

    const loadData = async () => {
        const response = await axios.get("http://localhost:5000/api/get2");
        setData(response.data);
    };

    useEffect(()=>{
        loadData();
    },[]);

    const deleteContact = (id) => {
        if(window.confirm("Are you sure you want to delete the card ?")
        ) {
            axios.delete(`http://localhost:5000/api/remove/${id}`);
            toast.success("Card Deleted Successfully");
            setTimeout(() => loadData(), 500);
        }
    }
  return (
    <div style={{marginTop:"150px"}}>
        <div className='doing-tb'>
        <table className='styled-table1'>
        <caption className='tab-cap'>In Progress</caption>
            <tbody>
                {data.map((item,index) => {
                    return(
                        <tr key = {item.id}>
                            <td>{item.title}</td>
                            <td>
                            <button className='btn btn-edit' id="basic-button"
                                aria-controls={open ? 'basic-menu' : undefined}
                                aria-haspopup="true"
                                aria-expanded={open ? 'true' : undefined}
                                onClick={handleClick}>Options
                                </button>
                                <Menu
                                    id="basic-menu"
                                    anchorEl={anchorEl}
                                    open={open}
                                    onClose={handleClose}
                                    MenuListProps={{
                                    'aria-labelledby': 'basic-button',
                                    }}
                                >
                                    <Link to={`/update/${item.id}`}><MenuItem onClick={handleClose}>Edit</MenuItem></Link>
                                    <MenuItem onClick={handleDelete}>Delete</MenuItem>
                                    <Link to={`/view/${item.id}`}><MenuItem onClick={handleClose}>View</MenuItem></Link>
                                    <MenuItem onClick={handleClose}>Back</MenuItem>
                                </Menu>
                            </td>
                        </tr>
                    )
                })}
            </tbody>
        </table>
        <Link to="/addContact">  <button className='btn btn-contact'>Add Card</button> </Link>
        </div>      
    </div>
    
  )
}

export default Home1;
