import React, {useHistory,useState} from "react";
import "./Signup.css"
import {Link} from "react-router-dom"
import axios from 'axios'

const Signup = () => {

    const history = useHistory();
    const [usernameReg, setUsernameReg] = useState('')
    const [passwordReg, setPasswordReg] = useState('')
    const [passwordConfirmReg, setPasswordConfirmReg] = useState('')

    const register = () => {
      axios.post("http://localhost:5000/register", {username: usernameReg, password: passwordReg}).then((response)=>{console.log(response);})
    }

  return (
    <div className="parent">
        <h1>Sign Up</h1>
        <form className="form">
          <div className="input-group">
            <label>Email</label>
            <input type="email" placeholder="Enter Email" onChange={(e)=>{setUsernameReg(e.target.value)}}/>
          </div>
          <div className="input-group">
            <label>Password</label>
            <input
              type="password"
              placeholder="Enter Password"
              onChange={(e)=>{setPasswordReg(e.target.value)}}
            />
          </div>
          <div className="input-group">
            <label>Confirm Password</label>
            <input
              type="password"
              placeholder="Enter Password"
              onChange={(e)=>{setPasswordConfirmReg(e.target.value)}}
            />
          </div>
          <button className="primary" onClick={register}>Sign Up</button>
          <p>Already Registered? <Link to="/login">Login </Link></p>
        </form>
      </div>
  )
}
export default Signup
