import React, {useEffect, useState} from 'react'
import {useHistory, useParams, Link} from "react-router-dom"
import "./Signup.css"
import axios from 'axios'
import { toast } from 'react-toastify'

const initialState = {
    title: "",
    des: "",
    status: "",
}

const Signup = () => {
    const [state, setState] = useState(initialState);

    const {title ,des ,status} = state;

    const history = useHistory();

    const {id} = useParams();

    useEffect(()=>{
        axios.get(`http://localhost:5000/api/get/${id}`).then((resp)=> setState({...resp.data[0]}))
    }, [id])

    const handleSubmit = (e) => {
        e.preventDefault();
        if(!title || !des) {
            toast.error("Please provide value into each input field")
        } else {
                axios.put(`http://localhost:5000/api/update/${id}`, {
                title,
                des,
                status,
            })
            .then(() => {
                setState({title: "", des: "", status: ""});
            })
            .catch((err)=> toast.error(err.response.data))
        toast.success("Card Edited Successfully")
            }
            
        setTimeout(() => history.push("/board"), 500);
        }

    const handleInputChange = (e) => {
        const {name, value} = e.target;
        setState({ ...state, [name]: value })
    }
  return (
    
                <div style={{marginTop: "100px"}}>
            <form style={{
                margin: "auto",
                padding: "50px",
                maxWidth: "400px",
                alignContent: "center"
            }}
            onSubmit={handleSubmit}
            >
                <label htmlFor='email'>Email</label>
                <input
                type='email'
                id='email'
                name='email'
                placeholder='Your Email...'
                onChange={handleInputChange}
                />
                <label htmlFor='password'>Password</label>
                <input
                type='password'
                id='password'
                name='password'
                placeholder='Your Password...'
                onChange={handleInputChange}
                />
                 <label htmlFor='password'>Confirm Password</label>
                <input
                type='password'
                id='confirm-password'
                name='password'
                placeholder='Your Password...'
                onChange={handleInputChange}
                />
                <input type="submit" className='background-blue' value="Sign Up" />
                <p className='center'>Already Registered? <Link to="/login">Login</Link></p>
            </form>
            </div>
  )
}
export default Signup
