import React, {useState} from 'react'
import {useHistory,Link} from "react-router-dom"
import "./Signup.css"
import axios from 'axios'
import { toast } from 'react-toastify'
import {Alert} from '@mui/material'
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';

const initialState = {
    username: "",
    password: "",
}

const Signup = () => {
    const [state, setState] = useState(initialState);

    const {username,password,confirmpassword} = state;

    const history = useHistory();

    const [error,setError] = useState("")

    const handleSubmit = (e) => {
      e.preventDefault();
      if(password !== confirmpassword){
        return setError("Passwords do not match")
      }if (password.length >20) {
      return setError("Password should contain less than 20 characters")
      }
      if (password.length <6) {
          return setError("Password should contain atleast 6 characters")
      }
      if (password.search(/[A-Z]/) === -1) {
          return setError("Enter a capital letter")
      }
      if (password.search(/[0-9]/) === -1) {
          return setError("Enter a number")
      }
      if (password.search(/[!@#$%^&*]/) === -1){
          return setError("Enter a special character")
      }else {
              axios.post("http://localhost:5000/api/register", {
                  username,
                  password,              
              })
              .then(() => {
                  setState({username: "", password: ""});
              })
              .catch((err)=> toast.error(err.response.data))
          toast.success("Signed in Successfully") 
        }        
      setTimeout(() => history.push("/board"), 500);
      }

    const handleInputChange = (e) => {
        const {name, value} = e.target;
        setState({ ...state, [name]: value })
    }
  return ( 
      <div style={{marginTop: "100px"}}>
        <Card variant="outlined" sx={{ maxWidth: 800}}>
      <CardContent>
            <form style={{
                margin: "auto",
                padding: "50px",
                maxWidth: "400px",
                alignContent: "center"
            }}
            onSubmit={handleSubmit}
            >
                <label>Email</label>
                <input
                type='email'
                id='username'
                name='username'
                placeholder='Your Email...'
                onChange={handleInputChange}
                />
                <label htmlFor='password'>Password</label>
                <input
                type='password'
                id='password'
                name='password'
                placeholder='Your Password...'
                onChange={handleInputChange}
                />
                 <label htmlFor='password'>Confirm Password</label>
                <input
                type='password'
                id='confirmpassword'
                name='confirmpassword'
                placeholder='Your Password...'
                onChange={handleInputChange}
                />
                {error && <Alert severity="error">{error}</Alert>}
                <input type="submit" className='background-blue' value="Sign Up" />
                <p className='center'>Already Registered? <Link to="/login">Login</Link></p>
            </form>
            </CardContent>
            </Card>
            </div>
            )    
}
export default Signup
