import React, {useState,useEffect} from 'react';
import {Link} from "react-router-dom";
import "./Todo.css";
import {toast} from "react-toastify"
import axios from "axios";
import VisibilityIcon from '@mui/icons-material/Visibility';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';

const Home = () => {
    const [data, setData] = useState([]); 

    const deleteContact = (id) => {
        if(window.confirm("Are you sure you want to delete the card ?")
        ) {
            axios.delete(`http://localhost:5000/api/remove/${id}`);
            toast.success("Card Deleted Successfully");
            setTimeout(() => loadData(), 500);
        }
    }

    const loadData = async () => {
        const response = await axios.get("http://localhost:5000/api/get1");
        setData(response.data);
    };

    useEffect(()=>{
        loadData();
    },[]);

  return (
    <div style={{marginTop:"150px"}}>
        <div className='todo-tb'>
        <table className='styled-table'>
        <caption className='tab-cap'>ToDo <Link to="/addCardTodo">  <button className='btn btn-contact'>Add Card</button> </Link></caption>
            <tbody>
                {data.map((item,index) => {
                    return(
                        <tr key = {item.id}>
                            <td>{item.title}</td>
                            <td>
                                <Link to={`/view/${item.id}`}><VisibilityIcon /></Link>
                                <Link to={`/update/${item.id}`}><EditIcon /></Link>
                                <DeleteIcon onClick={()=> deleteContact(item.id)} />
                            </td>
                        </tr>
                    )
                })}
            </tbody>
        </table>
        
        </div>      
    </div>
    
  )
}

export default Home;
