import React , {useEffect, useState} from 'react'
import Todo from './sec/Todo'
import Doing from './sec/Doing'
import Done from './sec/Done'
import "./Homepage.css"
import axios from 'axios'
import { Link , useHistory} from 'react-router-dom'


const Homepage = () => {
  const history = useHistory();

  const logout = () => {
    setLoginStatus(null);
    axios.get("http://localhost:5000/api/logout").then((response)=>{
      response.data.loggedIn = false 
    });
    history.push('/login')
  }

  axios.defaults.withCredentials = true;

  const [loginStatus,setLoginStatus] = useState()

  useEffect(()=>{
    axios.get("http://localhost:5000/api/login").then((response)=>{
      if (response.data.loggedIn === true) {
        setLoginStatus(response.data.user[0].username)
      }
    })
  }, [])
  return (
    <div>
      <div style={{
        backgroundColor: "ActiveCaption",

      }}>
      <h1>Workflow Management System</h1>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <Link to="/">Home</Link>
      <Link to="/board">Board</Link>
      <Link to="/signup">Signup</Link>
      <Link to="/login">Login</Link>
      <button onClick={logout}>Logout</button>
           </nav>
    <h4>User: {loginStatus}</h4>
    </div>
      <div>
        <Done/>
        <Todo/>
        <Doing/>
      </div>
    </div>
  )
}

export default Homepage
