import React, {useState,useEffect} from 'react';
import {Link} from "react-router-dom";
import "./Todo.css";
import {toast} from "react-toastify"
import axios from "axios";
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';


const Home = () => {
    const [data, setData] = useState([]); 

    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);
    const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

    const loadData = async () => {
        const response = await axios.get("http://localhost:5000/api/get1");
        setData(response.data);
    };

    useEffect(()=>{
        loadData();
    },[]);

    const deleteContact = (id) => {
        if(window.confirm("Are you sure you want to delete the card ?")
        ) {
            axios.delete(`http://localhost:5000/api/remove/${id}`);
            toast.success("Card Deleted Successfully");
            setTimeout(() => loadData(), 500);
        }
    }
  return (
    <div style={{marginTop:"150px"}}>
        <div className='todo-tb'>
        <table className='styled-table'>
        <caption className='tab-cap'>ToDo</caption>
            <tbody>
                {data.map((item,index) => {
                     const handleDelete = () => {
                        deleteContact(item.id);
                        setAnchorEl(null);
                      };
                    return(
                        <tr key = {item.id}>
                            <td>{item.title}</td>
                            <td>
                                <button className='btn btn-edit' id="basic-button"
                                aria-controls={open ? 'basic-menu' : undefined}
                                aria-haspopup="true"
                                aria-expanded={open ? 'true' : undefined}
                                onClick={handleClick}>Options
                                </button>
                                <Menu
                                    id="basic-menu"
                                    anchorEl={anchorEl}
                                    open={open}
                                    onClose={handleClose}
                                    MenuListProps={{
                                    'aria-labelledby': 'basic-button',
                                    }}
                                >
                                    <Link to={`/update/${item.id}`}><MenuItem onClick={handleClose}>Edit</MenuItem></Link>
                                    <MenuItem onClick={handleDelete}>Delete</MenuItem>
                                    <Link to={`/view/${item.id}`}><MenuItem onClick={handleClose}>View</MenuItem></Link>
                                    <MenuItem onClick={handleClose}>Back</MenuItem>
                                </Menu>
                            </td>
                        </tr>
                    )
                })}
            </tbody>
        </table>
        <Link to="/addCardTodo">  <button className='btn btn-contact'>Add Card</button> </Link>
        </div>      
    </div>
    
  )
}

export default Home;
