import React , {useEffect, useState} from 'react'
import axios from 'axios'
import { Link , useHistory} from 'react-router-dom'

const NewHome = () => {
  const history = useHistory();

  const logout = () => {
    axios.get("http://localhost:5000/api/logout").then((response)=>{
      response.data.loggedIn = false 
    });
    history.push('/login')
  }
  axios.defaults.withCredentials = true;

  const [loginStatus,setLoginStatus] = useState()

  useEffect(()=>{
    axios.get("http://localhost:5000/api/login").then((response)=>{
      if (response.data.loggedIn === true) {
        setLoginStatus(response.data.user[0].username)
      }
    })
  }, [])
  return (
    <div><div>
    <div style={{
      backgroundColor: "#019B79",
      color: "white",
      paddingTop: "10px"
    }}>
    <h1 style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold"}} >Workflow Management System</h1>
    <nav style={{
        color: "Yellow",
        alignContent: "center",
        textAlign:"center"
      }}>
      <Link style={{marginLeft: "20px",marginRight: "20px",marginBottom: "20px",fontWeight: "bold"}} to="/"> Home </Link>
      {loginStatus && (<Link style={{marginLeft: "20px",marginBottom: "20px",marginRight: "20px",fontWeight: "bold"}} to="/board"> Board </Link>)}
      {!loginStatus && (<Link style={{marginLeft: "20px",marginBottom: "20px",marginRight: "20px",fontWeight: "bold"}} to="/signup"> Signup </Link>)}
      <Link style={{marginLeft: "20px",marginRight: "20px",marginBottom: "20px",fontWeight: "bold"}} to="/login"> Login </Link>
      {loginStatus && (<button style={{marginLeft: "20px",marginBottom: "20px",marginRight: "20px",fontWeight: "bold"}} onClick={logout}> Logout </button>)} 
           </nav>
  </div>
  </div>
  </div>
  )
}

export default NewHome
