import React, {useState} from 'react'
import {useHistory,Link} from "react-router-dom"
import "./Login.css"
import axios from 'axios'
import { toast } from 'react-toastify'
import {Alert} from '@mui/material'

const initialState = {
    username: "",
    password: "",
}

const Login = () => {
    const [state, setState] = useState(initialState);

    const {username,password} = state;

    const history = useHistory();

    const [error,setError] = useState("Enter Your Email and Password")

    const handleSubmit = (e) => {
      e.preventDefault();
              axios.post("http://localhost:5000/api/login", {
                  username,
                  password,              
              })
              .then(() => {
                  setState({username: "", password: ""});
              })
              .catch((err)=> toast.error(err.response.data))
          toast.success("Logged in Successfully")        
      setTimeout(() => history.push("/board"), 500);
      }

    const handleInputChange = (e) => {
        const {name, value} = e.target;
        setState({ ...state, [name]: value })
    }
  return ( 
      <div style={{marginTop: "100px"}}>
            <form style={{
                margin: "auto",
                padding: "50px",
                maxWidth: "400px",
                alignContent: "center"
            }}
            onSubmit={handleSubmit}
            >
              <h1 style={{textAlign: "center"}}>Login</h1>
                <label>Email</label>
                <input
                type='email'
                id='username'
                name='username'
                placeholder='Your Email...'
                onChange={handleInputChange}
                required
                />
                <label htmlFor='password'>Password</label>
                <input
                type='password'
                id='password'
                name='password'
                placeholder='Your Password...'
                onChange={handleInputChange}
                required
                />
                {error && <Alert severity="error">{error}</Alert>}
                <input type="submit" className='background-blue' value="Log In" />
                <p className='center'>Not Registered? <Link to="/signup">Signup</Link></p>
            </form>
            </div>
            )    
}
export default Login
