import React , {useEffect, useState} from 'react'
import Todo from './sec/Todo'
import Doing from './sec/Doing'
import Done from './sec/Done'
import "./Homepage.css"
import axios from 'axios'
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';

const Homepage = () => {
  axios.defaults.withCredentials = true;

  const [loginStatus,setLoginStatus] = useState()

  useEffect(()=>{
    axios.get("http://localhost:5000/api/login").then((response)=>{
      if (response.data.loggedIn === true) {
        setLoginStatus(response.data.user[0].username)
      }
    })
  }, [])
  return (
    <div><div className='header'><h1>Workflow Management System</h1>
    <h4>User: {loginStatus}</h4>
          <Button >Login</Button>
      </div>
      <div>
        <Done/>
        <Todo/>
        <Doing/>
      </div>
    </div>
  )
}

export default Homepage
