import React, {useState} from "react";
import "./Login.css"
import {Link} from "react-router-dom"
import axios from 'axios'

const Login = () => {

    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [loginStatus, setLoginStatus] = useState('')

    const login = () => {
      axios.post("http://localhost:5000/login", {username: username, password: password}).then((response)=>{
        if(response.data.message){
          setLoginStatus(response.data.message);
        }else{
          setLoginStatus(response.data[0].username);
        }
      })
    }

  return (
    <div className="parent">
        <h1>Log In</h1>
        <form className="form">
          <div className="input-group">
            <label>Email</label>
            <input type="email" placeholder="Enter Email" onChange={(e)=>{setUsername(e.target.value)}}/>
          </div>
          <div className="input-group">
            <label>Password</label>
            <input
              type="password"
              placeholder="Enter Password"
              onChange={(e)=>{setPassword(e.target.value)}}
            />
          </div>
          <button className="primary" onClick={login}>Log In</button>
          <p>Not Registered? <Link to="/signup">Sign Up </Link></p>
        </form>
        <h3>{loginStatus}</h3>
      </div>
  )
}
export default Login
