import React , {useEffect, useState} from 'react'
import axios from 'axios'
import { Link , useHistory} from 'react-router-dom'


const NewHome = () => {
  const history = useHistory();

  const logout = () => {
    setLoginStatus(null);
    axios.get("http://localhost:5000/api/logout").then((response)=>{
      response.data.loggedIn = false 
    });
    history.push('/login')
  }
  axios.defaults.withCredentials = true;
  const [loginStatus,setLoginStatus] = useState()

  useEffect(()=>{
    axios.get("http://localhost:5000/api/login").then((response)=>{
      if (response.data.loggedIn === true) {
        setLoginStatus(response.data.user[0].username)
      }
    })
  }, [])
  
  return (
    <div>
    <div style={{
      backgroundColor: "#019B79",
      color: "white",
      paddingTop: "10px"
    }}>
    <h1 style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold"}} >Workflow Management System</h1>
    <nav style={{
        color: "Yellow",
        alignContent: "center",
        textAlign:"center"
      }}>
      <Link style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold"}} className='btn8' to="/"> Home </Link>
      {loginStatus && (<Link style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold",marginBottom: "10px"}} className='btn8' to="/board"> Board </Link>)}
      {!loginStatus && (<Link style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold"}} className='btn8' to="/signup"> Signup </Link>)}
      {!loginStatus && (<Link style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold"}} className='btn8' to="/login"> Login </Link>)}
      {loginStatus && (<button style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold"}} className='btn8'  onClick={logout}> Logout </button>)} 
           </nav>
           <h3 style={{marginLeft: "20px",marginRight: "20px",fontWeight: "bold"}}>User: {loginStatus}</h3>
  </div>
      <div>
        <div class="w3-container w3-content w3-center w3-padding-64" style={{textAlign: "center"}} id="band">
    <h2 class="w3-wide">THE BAND</h2>
    <p class="w3-opacity"><i>We love music</i></p>
    <p class="w3-justify">We have created a fictional band website. Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip
      ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum consectetur
      adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.</p>
    <div class="w3-row w3-padding-32">
      <div class="w3-third">
        <h2>Board</h2>
        <img src="https://www.linkpicture.com/q/board.png" class="w3-round w3-margin-bottom" alt="Random Name" style={{width: "100%",height: "100%"}}/>
      </div>
      <div class="w3-third">
        <h2>Signup</h2>
        <img src="https://www.linkpicture.com/q/signup.png" class="w3-round w3-margin-bottom" alt="Random Name" style={{width: "30%",height: "30%"}} />
      </div>
      <div class="w3-third">
        <h2>Login</h2>
        <img src="https://www.linkpicture.com/q/login_1.png" class="w3-round" alt="Random Name" style={{width: "30%",height: "30%"}}/>
      </div>
      <div class="w3-third">
        <h2>Actions</h2>
        <img src="https://www.linkpicture.com/q/actions.png" class="w3-round" alt="Random Name" style={{width: "15%",height: "15%"}}/>
      </div>
      <div style={{
      backgroundColor: "#019B79",
      color: "white",
      paddingBottom: "10px",
      paddingTop: "10px",
    }}>
      <p>Created by Vaibhav Shenoy</p>
      </div>
      </div>
      </div>
      </div>
  </div>
  )
}

export default NewHome
