import React, {useState,useEffect} from 'react';
import {Link} from "react-router-dom";
import "./Done.css";
import {toast} from "react-toastify"
import axios from "axios";


const Home2 = () => {
    const [data, setData] = useState([]); 

    const loadData = async () => {
        const response = await axios.get("http://localhost:5000/api/get3");
        setData(response.data);
    };

    useEffect(()=>{
        loadData();
    },[]);

    const deleteContact = (id) => {
        if(window.confirm("Are you sure you want to delete the card ?")
        ) {
            axios.delete(`http://localhost:5000/api/remove/${id}`);
            toast.success("Card Deleted Successfully");
            setTimeout(() => loadData(), 500);
        }
    }
  return (
    <div style={{marginTop:"150px"}}>
        <div className='done-tb'>
        <table className='styled-table2'>
        <caption className='tab-cap'>Completed</caption>
            <tbody>
                {data.map((item,index) => {
                    return(
                        <tr key = {item.id}>
                            <td>{item.title}</td>
                            <td>
                                <Link to={`/update/${item.id}`}>
                                    <button className='btn btn-edit'>Edit</button>
                                </Link>
                                <button className='btn btn-delete' onClick={() => deleteContact(item.id)}>Delete</button>
                                <Link to={`/view/${item.id}`}>
                                    <button className='btn btn-view'>View</button>
                                </Link>
                            </td>
                        </tr>
                    )
                })}
            </tbody>
        </table>
        <Link to="/addContact">  <button className='btn btn-contact'>Add Card</button> </Link>
        </div>      
    </div>
    
  )
}

export default Home2;
